INSERT INTO loan (product_id, min_interest_rate, max_interest_rate, max_loan_amount, description)
VALUES (694, 11.93, 19.85, "1억원", "[{'title': '대상', 'content': '직장인, 주부<br>ㆍ만 20세 이상, 건강보험 납부기준, 재직 3개월 이상 소득증빙이 가능한 급여소득자, 주부<br>(단, 채무불이행자, 과다채무자, 군미필자 제외) NICE신용평점 351점 이상', 'order': 1}, {'title': '금리', 'content': '연 11.93% ~ 19.85%', 'order': 2}, {'title': '한도', 'content': '300만원 ~1억원', 'order': 3}, {'title': '기간', 'content': '원리금균등분할상환: 12개월 ~ 96개월', 'order': 4}, {'title': '산출기준', 'content': '대출금리 = 기준금리(조달금리) + 가산금리<br>기준금리: 조달원가, 가산금리: 당행 내부등급 등에 따라 차등적용', 'order': 5}, {'title': '이자부과시기 ', 'content': '매월 후취(자동이체 가능)', 'order': 6}, {'title': '연체이율', 'content': '약정금리 + 3.0%(최대 20% 이내)<br>금리 및 수수료를 포함하여 법정이자율을 초과하지 않음', 'order': 7}, {'title': '중도상환조건(수수료)', 'content': '기한전상환대출금액 * 2% * (대출잔여일수/대출기간)<br>단, 여신기한전상환수수료는 대출개시일로부터 3년 이내 상환시 적용', 'order': 8}, {'title': '수수료 및 부대비용', 'content': '인지세: 5천만원 초과시 7만원(고객 50% 부담)', 'order': 9}, {'title': '만기 후 미상환', 'content': '대출금의 상환 또는 이자납입이 지연된 경우 연체이율이 적용되며 예금 등 기타채권과의 상계나 법적절차 등으로 재산상의 불이익을 당하실 수 있습니다.', 'order': 10}, {'title': '청약철회권 안내', 'content': '일반금융소비자는 계약 체결일, 계약서류를 받은날, 대출금 수령일로부터 14일 내에 계약에 대한 청약을 철회할 수 있습니다.(다만, 철회권을 행사하여 효력이 발생한 이후에는 이를 취소할 수 없습니다.)<br>청약철회를 위해서는 영업점, 인터넷뱅킹, 스마크뱅킹 등으로 저축은행에 청약 철회의 의사표시를 해야하며, 이미 수령한 대출금과 이에 대한 이자, 대출과 관련하여 저축은행이 제3자에게 부담한 인지세, 근저당권설정비용 등을 반환하여야 합니다. <br>대출계약 철회권을 행사한 경우에는 중도상환수수료가 면제되며, 5영업일 이내에 해댕 대출과 관련한 대출정보가 삭제됩니다. <br>대출계약 철회권을 남용하여 해당 저축은행을 대상으로 최근 1개월 내에 2회 이상 대출계약을 철회하는 경우, 신규대출·만기연장 거절, 대출한도 축소, 금리우대 제한 등 불이익이 발생할 수 있습니다.', 'order': 11}, {'title': '위법계약 해지안내', 'content': '금융회사가 「금융소비자 보호에 관한 법률」상 다음의 의무를 위반하여 대출계약을 체결한 경우, 금융소비자는 해당 계약을 위약금 등 수수료 부과없이 해지할 수 있습니다.<br>적합하지 아니하다고 인정되는 대출계약의 체결을 권유한 경우(법 제 17조제3항 위반)<br>대출상품이 적정하지 않은 경우에도 사실을 알리지 않거나 확인받지 않은 경우(법 제18조제2항 위반)<br>설명의무를 이행하지 않은 경우(법 제19조제1항·제3항 위반)<br>불공정영업행위를 한 경우(법 제20조제1항 위반)<br>부당권유행위를 한 경우(법 제21조 위반)<br>금융소비자는 위법계약해지권 행사를 위해 법 위반사실을 안 날로부터 1년 이내의 기간으로 계약체결일로부터 5년 이내에 계약 해지요구서에 위반사실을 입증하는 서류를 첨부하여 서면등(우편, 전자우편, 문자메세지 등) 으로 계약의 해지를 요구하여야 합니다.', 'order': 12}, {'title': '금리인하요구권안내', 'content': '금리인하요구권이란 금융소비자가 본인의 신용상태가 개선되었다고 판단되는 경우(취업·승진·재산증가·개인신용평점 상승 등) <br>저축은행에 자신이 적용받는 금리인하를 요구할 수 있는 권리(저축은행법 제14조의2)를 말합니다. <br>금리인하요구권은 영업점 방문 및 비대면채널(인터넷뱅킹, 모바일뱅킹, 콜센터 등)을 통해 신청가능하며(신청시기·횟수제한 없음),<br>저축은행은 금리인하를 요구하는 고객에게 신용상태 개선을 확인하는데 필요한 자료를 제출하도록 요구할 수 있습니다. <br>신용상태의 개선이 경미하거나 신용상태가 금리에 영향을 미치지 않는 상품에 대해 금리인하를 요구한 경우, 고객의 금리인하 요구는 수용되지 않을 수 있습니다. <br><br>저축은행은 금리인하 요구를 받은 날부터 10영업일 이내(고객에게 자료의 보완을 요구하는 날부터 자료가 제출되는 날까지의 기간은 포함되지 않습니다)에 금리인하 요구 수용 여부 및 그 사유를 알려드립니다.', 'order': 13}, {'title': '심의필 번호', 'content': '고려저축은행 준법감시인 심의필 2023-26호(2023.03.23.~2024.03.22.)<br>', 'order': 14}]");

